package nb.freddy.modules.dotnet;

import nb.freddy.modules.FreddyModuleBase;
import nb.freddy.modules.IndicatorTarget;
import nb.freddy.modules.SeverityRating;
import nb.freddy.modules.TargetPlatform;

/***********************************************************
 * Module targeting the native .NET BinaryFormatter API.
 * 
 * Written by Nicky Bloor (@NickstaDB).
 **********************************************************/
public class BinaryFormatterModule extends FreddyModuleBase {
	//ActivitySurrogateSelector payload data
	private static final int ACTSS_PAYLOAD_CMD_OFFSET = 17339;
	private static final String ACTSS_PREFIX_B64 = "";
	private static final String ACTSS_SUFFIX_B64 = "";
	private byte[] ACTSS_PAYLOAD;
	
	//PSObject payload data
	private static final int PSO_PAYLOAD_CMD_OFFSET = 1713;
	private static final String PSO_PREFIX_B64 = "";
	private static final String PSO_SUFFIX_B64 = "";
	private byte[] PSO_PAYLOAD;
	
	//TypeConfuseDelegate payload data
	private static final int TCD_PAYLOAD_CMD_OFFSET = 663;
	private static final String TCD_PREFIX_B64 = "";
	private static final String TCD_SUFFIX_B64 = "";
	private byte[] TCD_PAYLOAD;
	
	protected void initialiseModule() {
		setName("BinaryFormatter");
		setPlatform(TargetPlatform.DOTNET);
		setModuleIsRCECapable(true);
		setDescriptionCaveats("");
		setRemediationDetail("");
		setSeverity(SeverityRating.HIGH);
		
		registerPassiveScanIndicator(new byte[] {0x00, 0x01, 0x00, 0x00, 0x00, (byte)0xff}, IndicatorTarget.REQUEST);
		registerPassiveScanIndicator("BinaryFormatter.Deserialize", IndicatorTarget.RESPONSE);
		
		registerActiveScanExceptionPayload(new byte[] {0x00, 0x01, 0x00, 0x00, 0x00, (byte)0xff}, "BinaryFormatter.Deserialize");
		
		registerActiveScanCollaboratorPayload(PN_ACTSURSEL, true);
		registerActiveScanCollaboratorPayload(PN_PSOBJ, true);
		registerActiveScanCollaboratorPayload(PN_TYPCONDEL, true);
		
		//Initialise payload buffers
		ACTSS_PAYLOAD = buildBinaryPayloadBuffer(ACTSS_PREFIX_B64, ACTSS_SUFFIX_B64, true);
		PSO_PAYLOAD = buildBinaryPayloadBuffer(PSO_PREFIX_B64, PSO_SUFFIX_B64, false);
		TCD_PAYLOAD = buildBinaryPayloadBuffer(TCD_PREFIX_B64, TCD_SUFFIX_B64, false);
	}
	
	protected byte[] generateCollaboratorBytePayload(String payloadName, String hostname) {
		switch(payloadName) {
			case PN_ACTSURSEL:
				return generateBinaryPayload(ACTSS_PAYLOAD, ACTSS_PAYLOAD_CMD_OFFSET, "nslookup " + hostname, true);
				
			case PN_PSOBJ:
				return generateBinaryPayload(PSO_PAYLOAD, PSO_PAYLOAD_CMD_OFFSET, "nslookup " + hostname, false);
				
			case PN_TYPCONDEL:
				return generateBinaryPayload(TCD_PAYLOAD, TCD_PAYLOAD_CMD_OFFSET, "nslookup " + hostname, false);
		}
		return null;
	}
}
